# === STEP 3: Load Models ===
obj_3ddfa_path = "00017/00017_pcaTex.obj"  # or extractTex.obj
mononphm_txt_path = "mononphm_vertices.txt"  # exported from Blender

verts_3ddfa = load_obj_vertices(obj_3ddfa_path)
verts_mononphm = np.loadtxt(mononphm_txt_path)

print(f"Loaded 3DDFA-V3 vertices: {verts_3ddfa.shape}")
print(f"Loaded MonoNPHM vertices: {verts_mononphm.shape}")

# === STEP 4: Extract Only Lip Vertices ===
verts_3ddfa_lips = verts_3ddfa[lip_indices]
print(f"Lip Vertices from 3DDFA: {verts_3ddfa_lips.shape}")

# === STEP 5: Match Lip Region Points using Nearest Neighbors ===
nn = NearestNeighbors(n_neighbors=1).fit(verts_mononphm)
distances, indices = nn.kneighbors(verts_3ddfa_lips)

matched_mononphm_lips = verts_mononphm[indices[:, 0]]

# === STEP 6: Procrustes Alignment and Error Analysis (Lips Only) ===
mtx1, mtx2, disparity = procrustes(verts_3ddfa_lips, matched_mononphm_lips)
mse = mean_squared_error(mtx1, mtx2)

# === STEP 7: Report ===
print("\n=== LIP REGION COMPARISON RESULTS ===")
print(f"Number of Lip Vertices Compared: {len(verts_3ddfa_lips)}")
print(f"Procrustes Disparity (Lips Only): {disparity}")
print(f"Mean Squared Error (MSE, Lips Only): {mse:.8f}")
